CREATE OR REPLACE VIEW v_daily_sales AS
SELECT DATE(created_at) AS sale_date,
       SUM(total) AS gross_total,
       SUM(CASE WHEN paid THEN total ELSE 0 END) AS paid_total,
       COUNT(*) AS order_count
FROM orders
GROUP BY DATE(created_at)
ORDER BY sale_date;
